[["$r","MaxWidth","last",{"children":[["$r","h2",null,{"id":"declaring-custom-components","children":"Declaring custom components"}],"
",["$r","p",null,{"children":["You can declare custom components in ReactUnity. This is useful when you want to create a component that needs to interact with the underlying backend (",["$r","code",null,{"children":"ugui"}]," or ",["$r","code",null,{"children":"uitoolkit"}],")."]}],"
",["$r","p",null,{"children":["To declare a custom component, you need to create a new class extending ",["$r","code",null,{"children":"UGUIComponent"}]," or ",["$r","code",null,{"children":"UIToolkitComponent"}]," and register it in ",["$r","code",null,{"children":"UGUIContext.ComponentCreators"}]," or ",["$r","code",null,{"children":"UIToolkitContext.ComponentCreators"}]," respectively."]}],"
",["$r","h3",null,{"id":"example","children":"Example"}],"
",["$r","p",null,{"children":"First we create the custom component. For instance, we are declaring a custom button component that has a red background color."}],"
",["$r","pre",null,{"children":["$r","code",null,{"className":"language-cs","children":"using ReactUnity.Styling;
using ReactUnity.UGUI;
using UnityEngine;

public class CustomButtonComponent : ButtonComponent
{
    public CustomButtonComponent(UGUIContext context, Color backgroundColor) : base(context)
    {
        Style[StyleProperties.backgroundColor] = backgroundColor;
    }
}
"}]}],"
",["$r","p",null,{"children":["Then we need to register this component in ",["$r","code",null,{"children":"UGUIContext.ComponentCreators"}]," dictionary. This can be done in a ",["$r","code",null,{"children":"MonoBehaviour"}]," that is attached to the same ",["$r","code",null,{"children":"GameObject"}]," as ",["$r","code",null,{"children":"ReactRendererUGUI"}]," component.
The declaration must be done before ",["$r","code",null,{"children":"ReactRendererUGUI"}]," is enabled."]}],"
",["$r","pre",null,{"children":["$r","code",null,{"className":"language-cs","children":"using ReactUnity.UGUI;
using UnityEngine;

public class CustomComponentInitializer : MonoBehaviour
{
    public ReactRendererUGUI reactUnity;
    public Color customButtonColor;

    void Start()
    {
        UGUIContext.ComponentCreators["customButton"] = (type, text, context) => new CustomButtonComponent(context, customButtonColor);

        reactUnity.enabled = true;
    }
}
"}]}],"
",["$r","h3",null,{"id":"typescript-declaration","children":"Typescript declaration"}],"
",["$r","p",null,{"children":"You may also need to declare your custom component in Typescript"}],"
",["$r","pre",null,{"children":["$r","code",null,{"className":"language-ts","children":"declare global {
  interface ReactUnityCustomElements {
    mycomponent: { myprop?: number };
  }
}
"}]}],"
",["$r","p",null,{"children":"For example:"}],"
",["$r","pre",null,{"children":["$r","code",null,{"className":"language-ts","children":"declare global {
  interface ReactUnityCustomElements {
    button: Button;
  }
}
"}]}]]}]]
